(() => {
  // javascript/script.js
  jQuery(document).ready(function($) {
    if (window.location.href === "https://judifede.github.io/EducaAjedrez/") {
      const link = document.querySelector(".img_logo .elementor-widget-container a");
      link.removeAttribute("href");
    }
    function closeMenu(event) {
      var exceptionSelectors = [
        "path",
        "svg",
        ".hfe-nav-menu-icon",
        ".hfe-nav-menu__toggle"
      ];
      if (!exceptionSelectors.some(function(selector) {
        return event.target.matches(selector);
      })) {
        var $this = $(".hfe-nav-menu__toggle");
        var toggle_icon = $(".menu-main nav").data("toggle-icon");
        $(".menu-main").find(".hfe-nav-menu-icon").html(toggle_icon);
        $this.removeClass("hfe-active-menu");
        $this.attr("aria-expanded", "false");
        $(".menu-main nav").removeClass("menu-is-active");
      }
    }
    document.addEventListener("click", closeMenu);
  });
})();
